import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import './Login.css';

const Login = () => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');

    return (
        <div className="loginOuterContainer">
            <div className="loginInnerContainer">
                <h1 className = "heading">Login</h1>
                <div><input placeholder="Name" className="loginInput" type="text" onChange={(event) => setName(event.target.value)} /></div>
                <div><input placeholder="Room mt-20" className="loginInput" type="text" onChange={(event) => setRoom(event.target.value)} /></div>
                <Link onClick={event => (!name || !room) ? event.preventDefault() : null} to={`/chat?name=${name}&room=${room}`}>
                    <button className = {'button mt-20'} type="submit">Sign in</button>
                </Link>
            </div>
        </div>
    );
}

export default Login;
